import './BigCalendar.css';
import './Cards.css';
import { Link } from 'react-router-dom';
import Calendar from 'react-calendar'
import React, { useState } from 'react';
import 'react-calendar/dist/Calendar.css';
function BigCalendar() {
    const [chekindate, setCheckInDate] = useState(new Date());
    const [checkoutdate, setCheckOutDate] = useState(new Date());

    const oncheckoutdateChange = (newDate) => {
        setDate(newDate);
        console.log(newDate);
    }
    const onchekindateChange = (newDate) => {
        setCheckInDate(newDate);
        console.log(newDate);
    }
    return (
        <div className="container">
            <div class="ui four column centered grid">
                <div class="column">
                    <li className='cards__item'>
                        <Link className='cards__item__link' >
                            <div className='cards__item__info'>
                                <h5 className='cards__item__text'>Check IN</h5>
                            </div>
                            <Calendar
                                onChange={onchekindateChange}
                                value={chekindate}
                                showNeighboringMonth={false}
                                locale={"en-US"}
                            />
                        </Link>
                    </li>
                </div>
                <div class="column">
                    <li className='cards__item'>
                        <Link className='cards__item__link' >
                            <div className='cards__item__info'>
                                <h5 className='cards__item__text'>Check Out</h5>
                            </div>
                            <Calendar
                                onChange={oncheckoutdateChange}
                                value={checkoutdate}
                                showNeighboringMonth={false}
                                locale={"en-US"}
                            />
                        </Link>
                    </li>

                </div>

            </div>
            <div class="ui four column centered grid">
                <button class="ui primary button">
                    Save
            </button>
            </div>
        </div>
    );
}

export default BigCalendar;
