import './BigCalendar.css';
import './Cards.css';
import React, { Component } from 'react';
import { DatePicker, RangePicker, theme } from 'react-trip-date';
import { ThemeProvider } from 'styled-components';
function BigCalendar() {
    const handleResponsive = setNumberOfMonth => {
        let width = document.querySelector('.tp-calendar').clientWidth;
        if (width > 900) {
            setNumberOfMonth(3);
        } else if (width < 900 && width > 580) {
            setNumberOfMonth(2);
        } else if (width < 580) {
            setNumberOfMonth(1);
        }
    };

    const Day = ({ day }) => {
        return (
            <>
                <p className="date">{day.format('DD')}</p>
                <p className="date">7</p>
            </>
        );
    };
    onChange = date => console.log(date)
    return (
        <div className="container">
            <div class="ui four column centered grid">
                <div class="column">
                    <li className='cards__item'>
                        <Link className='cards__item__link' >
                            <div className='cards__item__info'>
                                <h5 className='cards__item__text'>Check IN</h5>
                            </div>
                            <Calendar
                                onChange={onchekindateChange}
                                value={chekindate}
                                showNeighboringMonth={false}
                                locale={"en-US"}
                            />
                        </Link>
                    </li>
                </div>
                <div class="column">
                    <li className='cards__item'>
                        <Link className='cards__item__link' >
                            <div className='cards__item__info'>
                                <h5 className='cards__item__text'>Check Out</h5>
                            </div>
                            <Calendar
                                onChange={oncheckoutdateChange}
                                value={checkoutdate}
                                showNeighboringMonth={false}
                                locale={"en-US"}
                            />
                        </Link>
                    </li>

                </div>

            </div>
            <div class="ui four column centered grid">
                <button class="ui primary button">
                    Save
            </button>
            </div>
        </div>
    );
}

export default BigCalendar;
