import './BigCalendar.css';
import './Cards.css';
import { Link } from 'react-router-dom';
import Calendar from 'react-calendar'
import 'react-calendar/dist/Calendar.css';

import DatePicker from 'react-dater'
import 'react-dater/dist/index.css'


function BigCalendar() {
    const [dates, setDates] = useState({

        checkin: new Date('2022-03-28'),

        checkout: new Date('2022-04-28')

    })
    return (
        <div className="container">
            <div class="ui four column centered grid">
                <div class="column">
                    <li className='cards__item'>
                        <Link className='cards__item__link' >
                            <div className='cards__item__info'>
                                <h5 className='cards__item__text'>Check IN</h5>
                            </div>
                            <DatePicker

                                dates={dates}

                                setDates={setDates}

                                open={open}

                                setOpen={setOpen}

                            >

                                <button onClick={() => setOpen(!open)}>

                                    {dates.checkin && dates.checkin.toDateString()} |{' '}

                                    {dates.checkout && dates.checkout.toDateString()}

                                </button>

                            </DatePicker>

                        </Link>
                    </li>
                </div>
                <div class="column">
                    <li className='cards__item'>
                        <Link className='cards__item__link' >
                            <div className='cards__item__info'>
                                <h5 className='cards__item__text'>Check Out</h5>
                            </div>

                        </Link>
                    </li>

                </div>

            </div>
            <div class="ui four column centered grid">
                <button class="ui primary button">
                    Save
            </button>
            </div>
        </div>
    );
}

export default BigCalendar;
